package com.continuuity.app.deploy;

import javax.annotation.Nullable;
import java.io.IOException;
import java.io.InputStream;

/**
 * Interface defining the response as returned by the execution of configure method.
 */
public interface ConfigResponse {
  /**˜                                            ˜
   * @return {@link java.io.InputStream} of configuration data as generated by invoking configure.
   */
  @Nullable
  public InputStream get() throws IOException;

  /**
   * @return true if success; false otherwise.
   */
  public int getExitCode();
}