package com.continuuity.app.deploy.internal;

import com.continuuity.app.deploy.ConfigResponse;

import java.io.Reader;

/**
 * This is implementation of {@link ConfigResponse}.
 * <p>
 *   Immutable class that hold exit code and stream of input.
 * </p>
 */
public class DefaultConfigResponse implements ConfigResponse {
  private final int exit;
  private final Reader reader;

  /**
   * Constructor.
   * @param exit code returned from processing a command.
   * @param reader that streams in data generated by running command.
   */
  public DefaultConfigResponse(int exit, Reader reader) {
    this.exit = exit;
    this.reader = reader;
  }

  /**
   * @return A handle to {@link Reader}
   */
  @Override
  public Reader get() {
    return reader;
  }

  /**
   * @return Exit code of command that was executed.
   */
  @Override
  public int getExitCode() {
    return exit;
  }
}
