package com.continuuity.overlord.metrics.client;

import java.util.List;

/**
 * RecordCollector interface for collecting the records generated by the
 * sources.
 */
public interface RecordCollector {
  /**
   * Adds a record to the collectors
   * @param info Information about record
   * @return {@link RecordBuilder}
   */
    public RecordBuilder addRecord(Info info );

  /**
   * Adds a record to the collectors
   * @param name Name of the record
   * @return {@link RecordBuilder}
   */
    public RecordBuilder addRecord(String name);

  /**
   * Clears the state of collectors.
   */
  public void clear();

  /**
   * @return List of Record
   */
  public List<Record> getRecords();

}
